import styled from 'styled-components';
import logo from '../../static/logo.png';
export const HeaderWrap=styled.div`
	position:relative;
	top:0px;
	left:50%;
	margin-left:-50%;
	width:100%;
	height:60px;
	border-bottom:1px solid #eee;
`;
export const Logo=styled.a.attrs({href :'/'})`
	position:absolute;
	top:0px;
	left:0px;
	display:block;
	width:100px;
	height:60px;
	background:url(${logo});
	background-size:100%;
	background-position:center;
	background-repeat:no-repeat;

`;
export const Nav=styled.div`
	position:absolute;
	top:0px;
	left:170px;
	width:960px;
	height:100%;
`;
export const NavItem=styled.div`
font-size:16px;
line-height:60px;
padding:0px 20px;
color:#333;

	&.left{
		float:left;
	}
	&.right{
		float:right;
		color:#969696;
	}
	&.active{
		color:#ea6f5a;
	}
`;
export const NavInput=styled.input.attrs({
	placeholder:'搜索一下'
})`
	width:160px;
	height:40px;
	border:0px;
	outLine:0px;
	border-radius:20px;
	padding:0px 40px 0px 15px;
	margin-top:10px;
	margin-left:20px;
	box-sizing:border-box;
	background-color:#eee;
	font-size:14px;
	&::placeholder{
		color:#999;
	}
`;
export const Add=styled.div`
	position:absolute;
	right:0px;
	top:0px;
	height:60px;
`;
export const Reg=styled.div`
	float:left;
	height:60px;
`;
export const Wri=styled.div`
	float:left;
	height:60px;
`;
